import React, {useEffect, useState} from "react";
import Navbar from "../../Components/Navbar/Navbar";
import Phone from "../../Components/Phone/Phone";
import "./Home.css"
import Console from "../../Components/Console/Console";

const Home = (props) => {
    const [isMobile, setIsMobile] = useState(false);
    const [activeDevice, setActiveDevice] = useState('phone');

    useEffect(() => {
        document.title = 'Home • Sahil';

        const handleResize = () => {
            const mobile = window.innerWidth <= 992;
            setIsMobile(mobile);
        };

        // Initial check
        handleResize();

        // Add event listener
        window.addEventListener('resize', handleResize);

        // Cleanup
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    return (
        <div>
            <Navbar active="Home"/>

            {isMobile && (
                <div className="device-switcher">
                    <button 
                        className={activeDevice === 'phone' ? 'active' : ''}
                        onClick={() => setActiveDevice('phone')}
                    >
                        Phone View
                    </button>
                    <button 
                        className={activeDevice === 'console' ? 'active' : ''}
                        onClick={() => setActiveDevice('console')}
                    >
                        Console View
                    </button>
                </div>
            )}

            <div className="wrapper">
                {!isMobile ? (
                    // Desktop view - show both components
                    <>
                        <Phone />
                        <Console display={props.display} setDisplay={props.setDisplay} />
                    </>
                ) : (
                    // Mobile view - show based on selected device
                    <>
                        {activeDevice === 'phone' && <div className="mobile-device"><Phone /></div>}
                        {activeDevice === 'console' && <div className="mobile-device"><Console display={props.display} setDisplay={props.setDisplay} /></div>}
                    </>
                )}
            </div>
        </div>
    );
};

export default Home;
