import React, {useEffect, useState} from "react";
import Navbar from "../../Components/Navbar/Navbar";
import Phone from "../../Components/Phone/Phone";
import "./Home.css"
import Console from "../../Components/Console/Console";

const Home = (props) => {
    const [isMobile, setIsMobile] = useState(false);

    useEffect(() => {
        document.title = 'Home • Sahil';

        // Check if we're on mobile and update state
        const checkScreenSize = () => {
            setIsMobile(window.innerWidth <= 992);
        };

        // Initial check
        checkScreenSize();

        // Add event listener for window resize
        window.addEventListener('resize', checkScreenSize);

        // Handle device selector buttons
        const handleDeviceSelect = () => {
            const btns = document.querySelectorAll('.device-btn');
            const phoneContainer = document.querySelector('.phone-container');
            const consoleContainer = document.querySelector('.console-container');

            btns.forEach(btn => {
                btn.addEventListener('click', () => {
                    // Remove active class from all buttons
                    btns.forEach(b => b.classList.remove('active'));

                    // Add active class to clicked button
                    btn.classList.add('active');

                    const device = btn.getAttribute('data-device');

                    if (device === 'phone') {
                        phoneContainer.style.display = 'block';
                        consoleContainer.style.display = 'none';
                    } else if (device === 'console') {
                        phoneContainer.style.display = 'none';
                        consoleContainer.style.display = 'block';
                    }
                });
            });
        };

        if (isMobile) {
            setTimeout(handleDeviceSelect, 0);
        }

        // Clean up
        return () => {
            window.removeEventListener('resize', checkScreenSize);
        };
    }, [isMobile]);


    return (
        <div>
            <Navbar active="Home"/>
            <div className="wrapper">
                {isMobile && <div className="mobile-device-selector">
                    <button className="device-btn active" data-device="phone">Phone View</button>
                    <button className="device-btn" data-device="console">Console View</button>
                </div>}

                <div className={`phone-container ${isMobile ? 'mobile-view' : ''}`}>
                    <Phone/>
                </div>

                <div className={`console-container ${isMobile ? 'mobile-view' : ''}`}>
                    <Console display={props.display} setDisplay={props.setDisplay}/>
                </div>
            </div>
        </div>
    )
}

export default Home;
